# proeblem link : https://www.codetree.ai/missions/8/problems/subsequences-with-few-overlapping-elements?&utm_source=clipboard&utm_medium=text

from collections import defaultdict

n, k = tuple(map(int, input().split()))
arr = [0] + list(map(int, input().split()))

count_array = defaultdict(lambda: 0)

ans = 0
j = 0

for i in range(1, n+1):
    while j + 1 <= n and count_array[arr[j + 1]] < k:
        count_array[arr[j+1]] = count_array[arr[j+1]] + 1
        j += 1

    ans = max(ans, j - i + 1)

    count_array[arr[i]] -= 1

print(ans)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            